import React from "react";
import { useParams, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import styled from "styled-components";
import PrimaryButton from "./PrimaryButton";

export interface Project {
  id: string;
  name: string;
  shortdescription: string;
  description: string;
  image: string;
  url: string;
  languages: string[];
  participants: string[];
}

type Params = {
  projectId: string;
};

function Work() {
  const [projects, setProjects] = useState<Project[]>([]);
  const [project, setProject] = useState<Project | null>(null);
  const { projectId } = useParams<Params>();
  useEffect(() => {
    fetch("/klara.json")
      .then((response) => response.json())
      .then((result) => {
        setProjects(result.work);
        console.log(result.work);
      });
  }, []);

  console.log("Projects:", projects);

  useEffect(() => {
    if (projects !== undefined) {
      setProject(projects.find((project) => project.id === projectId) || null);
    }
  }, [projects, projectId]);

  return (
    <main>
      <HeaderDiv>
        <h1>Work</h1>
        <div>
          <hr />
        </div>
      </HeaderDiv>
      {project !== null && (
        <WorkDiv>
          <div>
            <p>{project.description}</p>
            <PrimaryButton type="button">
              <Link to={project.url}>Go to website</Link>
            </PrimaryButton>
          </div>
          <div>
            {/* <img src={project.image}></img> */}
            <h2>{project.name}</h2>
            {project.languages.map((language) => (
              <strong key={language}>/ {language} </strong>
            ))}
            {project.participants !== null && (
              <div>
                <p>Created together with:</p>
                {project.participants.map((participant) => (
                  <span key={participant}> {participant}, </span>
                ))}
              </div>
            )}
          </div>
        </WorkDiv>
      )}
    </main>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 70%;
  }

  hr {
    margin-bottom: 25px;
  }
`;

const WorkDiv = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 60px;

  div {
    width: 50%;
    margin-right: 70px;
  }

  h2 {
    font-size: 4rem;
  }

  img {
    width: 400px;
    height: 400px;
  }
`;

export default Work;
