import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import styled from 'styled-components';
import PrimaryButton from './PrimaryButton';
import { AnimatePresence, motion } from 'framer-motion';

export interface Project {
  id: string;
  name: string;
  shortdescription: string;
  description: string;
  image: string;
  url: string;
  languages: string[];
  participants: string[];
}

type Params = {
  projectId: string;
};

function Work() {
  const [projects, setProjects] = useState<Project[]>([]);
  const [project, setProject] = useState<Project | null>(null);
  const { projectId } = useParams<Params>();
  useEffect(() => {
    fetch('/klara.json')
      .then((response) => response.json())
      .then((result) => {
        setProjects(result.work);
        console.log(result.work);
      });
  }, []);

  console.log('Projects:', projects);

  useEffect(() => {
    if (projects !== undefined) {
      setProject(projects.find((project) => project.id === projectId) || null);
    }
  }, [projects, projectId]);

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <main>
          <HeaderDiv>
            <h1>Work</h1>
            <div>
              <hr />
            </div>
          </HeaderDiv>
          {project !== null && (
            <WorkDiv>
              <div>
                <p>{project.description}</p>
                <div>
                  {project.languages.map((language) => (
                    <strong key={language}>/ {language} </strong>
                  ))}
                </div>
                <PrimaryButton type='button'>
                  Go to website/ TBD
                  {/*---Funkar inte Ã¤n!----
                   <a href={project.url} target='_blank'>
                    Go to website
                  </a> */}
                </PrimaryButton>
              </div>
              <div>
                <img src={project.image}></img>
                {project.participants !== null && (
                  <div>
                    <h3>Created together with:</h3>
                    <ul>
                      {project.participants.map((participant) => (
                        <li key={participant}> {participant} </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </WorkDiv>
          )}
        </main>
      </motion.div>
    </AnimatePresence>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;

  div {
    width: 70%;
    padding-bottom: 1.2%;
  }

  @media (max-width: 500px) {
    div {
      width: 60%;
      padding-bottom: 0;
    }
  }
`;

const WorkDiv = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 60px;

  div {
    width: 50%;
    margin-right: 70px;
    margin-bottom: 20px;
  }

  div > div {
    width: 100%;
    margin: 20px 0 20px 0;
  }

  h3 {
    font-size: calc(90% + 1.1vw);
    text-transform: uppercase;
  }

  img {
    width: 400px;
  }

  @media (max-width: 850px) {
    flex-direction: column-reverse;

    div {
      width: 90%;
    }
  }

  @media (max-width: 500px) {
    img {
      width: 90%;
      min-width: 250px;
    }
  }
`;

export default Work;
