import React from 'react';
import { useEffect, useState } from 'react';
import moment, { Moment } from 'moment';
import { useContext } from 'react';
import { SomeContext } from './SomeContext';
import styled from 'styled-components';
import BlogForm from './BlogForm';
import { AnimatePresence, motion, easeInOut } from 'framer-motion';

interface Post {
  title: string;
  date: Moment;
  content: string;
}

interface MyComponentProps {
  ThemeColor: string;
}

function BlogView() {
  const { loggedIn } = useContext(SomeContext) ?? {};
  const blog = useContext(SomeContext)?.blog;
  const { theme } = useContext(SomeContext) ?? {};
  const [posts, setPosts] = useState<Post[]>([]);
  const [isNotLoggedIn, setIsNotLoggedIn] = useState(true);
  const [showForm, setShowForm] = useState(false);

  const storedPosts = localStorage.getItem('posts');

  useEffect(() => {
    if (blog) {
      const blogList = blog.map((post) => ({
        title: post.title,
        date: moment(post.date),
        content: post.content,
      }));
      localStorage.setItem('posts', JSON.stringify(blogList));
      setPosts(blogList);
    }
  }, [blog]);

  useEffect(() => {
    if (storedPosts) {
      setPosts(JSON.parse(storedPosts));
    }
  }, [storedPosts]);

  useEffect(() => {
    if (loggedIn) {
      setIsNotLoggedIn(false);
    }
  }, [loggedIn]);

  function handleClick() {
    setShowForm(true);
  }

  useEffect(() => {
    console.log(posts);
  }, [posts]);

  return (
    <AnimatePresence>
      <motion.div
        initial={{ x: 200, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        exit={{ x: -300, opacity: 0 }}
        transition={{ delay: 0.3, ease: easeInOut, duration: 1.3 }}
      >
        <main>
          <HeaderDiv>
            <h1>Blog</h1>
            <div>
              <hr />
            </div>
          </HeaderDiv>
          {posts.map((post) => (
            <BlogDiv key={post.title}>
              <h2>{post.title}</h2>
              <p>{moment(post.date).format('YYYY-MM-DD')}</p>
              <p>{post.content}</p>
              <hr />
            </BlogDiv>
          ))}
          <FormDiv>
            {showForm ? (
              <BlogForm blogPosts={posts} />
            ) : (
              <AddButton
                ThemeColor={
                  theme === 'light' ? 'rgb(42, 42, 42)' : 'antiquewhite'
                }
                onClick={handleClick}
                disabled={isNotLoggedIn ? true : false}
              >
                Add blogpost
              </AddButton>
            )}
          </FormDiv>
        </main>
      </motion.div>
    </AnimatePresence>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 70%;
  }

  hr {
    margin-bottom: 25px;
  }
`;

const BlogDiv = styled.div`
  margin-top: 60px;

  h2 {
    font-size: 4rem;
  }

  p {
    width: 80%;
  }

  hr {
    margin: 40px 0 40px 0;
  }
`;

const FormDiv = styled.div`
  margin-top: 60px;
  display: flex;
  justify-content: center;
`;

const AddButton = styled.button<MyComponentProps>`
  height: 40px;
  width: 180px;
  background-color: inherit;
  border: 1.5px solid ${(props) => props.ThemeColor};
  border-radius: 30px;
  font-size: 0.9rem;
  transition: 0.2s ease-in;

  &:hover {
    background-color: rgb(254, 105, 5);
  }
`;

export default BlogView;
