import React from "react";
import { useEffect, useState } from "react";
// import Moment from "react-moment";
import moment, { Moment } from "moment";
import { useContext } from "react";
import { SomeContext } from "./SomeContext";
import type { Blog } from "./SomeContext";
import styled from "styled-components";
import PrimaryButton from "./PrimaryButton";

interface Post {
  title: string;
  date: Moment;
  content: string;
}

function BlogView() {
  const { loggedIn } = useContext(SomeContext) ?? {};
  const blog = useContext(SomeContext)?.blog;
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [posts, setPosts] = useState<Post[]>([]);
  const [isNotLoggedIn, setIsNotLoggedIn] = useState(true);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    if (blog) {
      const blogList = blog.map((post) => ({
        title: post.title,
        date: moment(post.date),
        content: post.content,
      }));
      setPosts(blogList);
    }
  }, [blog]);

  useEffect(() => {
    if (loggedIn) {
      setIsNotLoggedIn(false);
    }
  }, [loggedIn]);

  const thisDate = moment().format("YYYY-MM-DD");

  function handleSubmit() {
    if (loggedIn) {
      setPosts([
        { title: title, date: moment(thisDate), content: content },
        ...posts,
      ]);
      console.log(title);
      console.log(posts);
    }
  }

  function handleClick() {
    setShowForm(true);
  }

  useEffect(() => {
    console.log(posts);
  }, [posts]);

  return (
    <main>
      <HeaderDiv>
        <h1>Blog</h1>
        <div>
          <hr />
        </div>
      </HeaderDiv>
      {posts.map((post) => (
        <BlogDiv key={post.title}>
          <h2>{post.title}</h2>
          <p>{post.date.format("YYYY-MM-DD")}</p>
          <p>{post.content}</p>
          <hr />
        </BlogDiv>
      ))}
      {showForm && (
        <button onClick={handleClick} disabled={isNotLoggedIn ? true : false}>
          Add blogpost
        </button>
      )}

      <Form onSubmit={handleSubmit}>
        <label>
          Title
          <input
            onChange={(event) => setTitle(event.target.value)}
            value={title}
          />
        </label>
        <label>
          Content
          <textarea
            rows={4}
            onChange={(event) => setContent(event.target.value)}
            value={content}
          />
        </label>
        <PrimaryButton type="submit">Publish</PrimaryButton>
      </Form>
    </main>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 70%;
  }

  hr {
    margin-bottom: 25px;
  }
`;

const BlogDiv = styled.div`
  margin-top: 60px;

  h2 {
    font-size: 4rem;
  }

  p {
    width: 80%;
  }

  hr {
    margin: 40px 0 40px 0;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 350px;

  label {
    width: 100%;
    display: flex;
    flex-direction: column;
    border-bottom: 1px solid black;
    margin-bottom: 20px;
  }

  input {
    width: 250px;
    background-color: inherit;
    outline: none;
    border: none;
    padding: 5px;
  }

  textarea {
    width: 250px;
    background-color: inherit;
    outline: none;
    border: none;
    padding: 5px;
  }
`;

export default BlogView;
