import React from 'react';
// import { useEffect, useState } from "react";
// import moment from "moment";
import { useContext } from 'react';
import { SomeContext } from './SomeContext';
import styled from 'styled-components';
import { AnimatePresence, easeInOut, motion } from 'framer-motion';

function AboutView() {
  const details = useContext(SomeContext)?.details;
  const misc = useContext(SomeContext)?.misc;
  // const [years, setYears] = useState("");

  // useEffect(() => {
  //   if (details) {
  //     const diffYears = moment().diff(details.age, "years") * 1;
  //     setYears(diffYears.toString());
  //   }
  // }, [details]);

  return (
    <AnimatePresence mode='wait'>
      <motion.div
        initial={{ x: 200, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        exit={{ x: -300, opacity: 0 }}
        transition={{ delay: 0.3, ease: easeInOut, duration: 1.3 }}
      >
        <main>
          <HeaderDiv>
            <h1>About</h1>
            <div>
              <hr />
            </div>
          </HeaderDiv>
          <AboutDiv>
            {misc && misc !== null && (
              <div>
                <IntroParagraph>{misc.introdescription}</IntroParagraph>
                {misc.description.map((paragraph, index) => (
                  <p key={index}>{paragraph}</p>
                ))}
              </div>
            )}
            {details && details !== null && (
              <ImageDiv>
                <img alt='Klara' src={details.image[0]}></img>
                <SkillDiv>
                  {misc && misc !== null && (
                    <ul>
                      {misc.skills.map((skill) => (
                        <li key={skill}>/ {skill} </li>
                      ))}
                    </ul>
                  )}
                </SkillDiv>
                {/* <h2>{details.name}</h2> */}
                {/* {years !== "" && <p>{years} years</p>}
            <ul>
              <li>{details.email}</li>
            </ul> */}
              </ImageDiv>
            )}
          </AboutDiv>
        </main>
      </motion.div>
    </AnimatePresence>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 70%;
  }

  hr {
    margin-bottom: 25px;
  }
`;

const AboutDiv = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 60px;

  div {
    width: 50%;
    padding-right: 70px;
  }

  p:nth-child(5),
  p:nth-child(8) {
    /* font-size: 1.3rem; */
    font-size: 1.7vw;
    font-family: NewYork, serif;
  }

  @media (max-width: 1000px) {
    flex-direction: column-reverse;

    div {
      width: 100%;
    }
  }
`;

const IntroParagraph = styled.p`
  /* font-size: 2rem; */
  font-size: 2.3vw;
  font-family: NewYork, serif;
`;

const ImageDiv = styled.div`
  @media (max-width: 1000px) {
    display: flex;
    flex-direction: row-reverse;
    justify-content: space-around;
  }
`;

const SkillDiv = styled.div`
  width: 97% !important;
  padding: 0 !important;

  ul {
    display: flex;
    flex-direction: column;
    align-items: flex-end;
  }

  li {
    margin-top: 10px;
    font-weight: bold;
  }
`;

export default AboutView;
