import React from "react";
import { useEffect, useState } from "react";
// import Moment from "react-moment";
import moment, { Moment } from "moment";
import { useContext } from "react";
import { SomeContext } from "./SomeContext";
import type { Blog } from "./SomeContext";

interface Post {
  title: string;
  date: Moment;
  content: string;
}

function Blog() {
  const blog = useContext(SomeContext)?.blog;
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [posts, setPosts] = useState<Post[]>([]);

  useEffect(() => {
    if (blog) {
      const blogList = blog.map((post) => ({
        title: post.title,
        date: moment(post.date),
        content: post.content,
      }));
      setPosts(blogList);
    }
  }, [blog]);

  const thisDate = moment().format("YYYY-MM-DD");

  function handleSubmit() {
    setPosts([
      { title: title, date: moment(thisDate), content: content },
      ...posts,
    ]);
    console.log(title);
    console.log(posts);
  }

  useEffect(() => {
    console.log(posts);
  }, [posts]);

  return (
    <main>
      <h1>Blog</h1>
      <form onSubmit={handleSubmit}>
        <input
          onChange={(event) => setTitle(event.target.value)}
          value={title}
          placeholder="Title"
        />
        <input
          onChange={(event) => setContent(event.target.value)}
          value={content}
          placeholder="Content"
        />
        <button type="submit">Publish</button>
      </form>
      {posts.map((post) => (
        <div key={post.title}>
          <h2>{post.title}</h2>
          <p>{post.date.format("YYYY-MM-DD")}</p>
          <p>{post.content}</p>
        </div>
      ))}
    </main>
  );
}

export default Blog;
