// import { useState } from "react";
// import { Link } from "react-router-dom";
// import * as Icon from "react-bootstrap-icons";
import './Home.css';
import WorkList from './WorkList';
import Contact from './ContactComponent';
import PrimaryButton from './PrimaryButton';
import styled from 'styled-components';
import { useContext, useRef, useEffect } from 'react';
import { SomeContext } from './SomeContext';
import {
  AnimatePresence,
  motion,
  easeInOut,
  useInView,
  useAnimation,
} from 'framer-motion';

// import axios from "axios";

// const Skills = styled.div`
//   padding: 10px;
//   background-color: orange;
//   position: relative;
//   width: 100vw;
//   overflow-x: hidden;
//   height: 3rem;
// `;

function Home() {
  const ref = useRef(null);
  const isInView = useInView(ref);
  const animation = useAnimation();
  const details = useContext(SomeContext)?.details;
  const misc = useContext(SomeContext)?.misc;
  const projects = useContext(SomeContext)?.work;

  function handleClick() {
    const element = document.querySelector(`#${Contact.props.id}`);
    element?.scrollIntoView({ behavior: 'smooth' });
  }

  useEffect(() => {
    if (isInView) {
      animation.start({
        y: 0,
        transition: {
          ease: easeInOut,
        },
      });
    }
    console.log('elementet syns', isInView);
  }, [isInView]);

  return (
    <AnimatePresence mode='wait'>
      <motion.div
        initial={{ x: 200, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        exit={{ x: -300, opacity: 0 }}
        transition={{ delay: 0.3, ease: easeInOut, duration: 1.3 }}
      >
        <main>
          <IntroDiv>
            {details && details !== null && (
              <img alt='Klara-home' src={details.image[0]}></img>
            )}
            <div>
              <h1>Hello!</h1>
              {misc && misc !== null && (
                <p>{misc.bio}</p>
                //     {/* <Skills>
                //   <ul className="Home-skill-primary">
                //     {misc.skills.map((skill) => (
                //       <li className="Home-skill-item" key={skill}>
                //         {skill}
                //       </li>
                //     ))}
                //   </ul>
                //   <ul className="Home-skill-secondary">
                //     {misc.skills.map((skill) => (
                //       <li className="Home-skill-item" key={skill}>
                //         {skill}
                //       </li>
                //     ))}
                //   </ul>
                // </Skills> */}
              )}
              <PrimaryButton type='button' onClick={handleClick}>
                Contact me!
              </PrimaryButton>
            </div>
          </IntroDiv>

          <CategoryDiv>
            <h2>Work</h2>
            <div>
              <hr />
            </div>
          </CategoryDiv>
          {projects && <WorkList projects={projects} />}

          <AnimatePresence>
            <motion.div initial={{ y: 100 }} animate={animation}>
              <CategoryDiv ref={ref}>
                <h2>Contact</h2>
                <div>
                  <hr />
                </div>
              </CategoryDiv>
            </motion.div>
          </AnimatePresence>
          <Contact id='contact-section' />
        </main>
      </motion.div>
    </AnimatePresence>
  );
}

const IntroDiv = styled.div`
  display: flex;
  flex-direction: row;

  h1 {
    font-size: 10rem;
  }

  div {
    margin-left: 20px;
  }

  p {
    width: 60%;
  }

  img {
    width: 400px;
    height: 400px;
    object-fit: cover;
  }
`;

const CategoryDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 70%;
  }

  hr {
    margin-bottom: 25px;
  }
`;

export default Home;
