import React from "react";
import { useEffect, useState } from "react";
// import Moment from "react-moment";
import moment, { Moment } from "moment";
import { useContext } from "react";
import { SomeContext } from "./SomeContext";
import type { Blog } from "./SomeContext";
import styled from "styled-components";

interface Post {
  title: string;
  date: Moment;
  content: string;
}

function BlogView() {
  const blog = useContext(SomeContext)?.blog;
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [posts, setPosts] = useState<Post[]>([]);

  useEffect(() => {
    if (blog) {
      const blogList = blog.map((post) => ({
        title: post.title,
        date: moment(post.date),
        content: post.content,
      }));
      setPosts(blogList);
    }
  }, [blog]);

  const thisDate = moment().format("YYYY-MM-DD");

  function handleSubmit() {
    setPosts([
      { title: title, date: moment(thisDate), content: content },
      ...posts,
    ]);
    console.log(title);
    console.log(posts);
  }

  useEffect(() => {
    console.log(posts);
  }, [posts]);

  return (
    <main>
      <HeaderDiv>
        <h1>Blog</h1>
        <div>
          <hr />
        </div>
      </HeaderDiv>
      {posts.map((post) => (
        <div key={post.title}>
          <h2>{post.title}</h2>
          <p>{post.date.format("YYYY-MM-DD")}</p>
          <p>{post.content}</p>
        </div>
      ))}
      <form onSubmit={handleSubmit}>
        <input
          onChange={(event) => setTitle(event.target.value)}
          value={title}
          placeholder="Title"
        />
        <input
          onChange={(event) => setContent(event.target.value)}
          value={content}
          placeholder="Content"
        />
        <button type="submit">Publish</button>
      </form>
    </main>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 70%;
  }

  hr {
    margin-bottom: 25px;
  }
`;

export default BlogView;
