import React from 'react';
import moment, { Moment } from 'moment';
import { useState, useContext } from 'react';
import styled from 'styled-components';
import PrimaryButton from './PrimaryButton';
import { SomeContext } from './SomeContext';
import { useLocalStorage } from './useLocalStorage';

interface Post {
  title: string;
  date: Moment;
  content: string;
}

interface MyComponentProps {
  ThemeColor: string;
}

type BlogFormProps = {
  blogPosts: Post[];
};

function BlogForm(props: BlogFormProps) {
  const { loggedIn } = useContext(SomeContext) ?? {};
  const { theme } = useContext(SomeContext) ?? {};
  // const [posts, setPosts] = useState<Post[]>(props.blogPosts);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [posts, setPosts] = useLocalStorage('posts', props.blogPosts);

  // function handleSubmit(event: React.FormEvent<HTMLFormElement>)
  function handleSubmit() {
    // event?.preventDefault();
    if (loggedIn) {
      const newPost = {
        title: title,
        date: moment(thisDate),
        content: content,
      };
      // setPosts((prevPosts) => {
      //   const updatedPosts = [newPost, ...prevPosts];
      //   localStorage.setItem('posts', JSON.stringify(updatedPosts));
      //   return updatedPosts;
      // });
      // setTitle('');
      // setContent('');
      setPosts([newPost, ...posts]);
      localStorage.setItem('posts', JSON.stringify([newPost, ...posts]));
      setTitle('');
      setContent('');
    }
  }

  const thisDate = moment().format('YYYY-MM-DD');

  return (
    <Form
      ThemeColor={theme === 'light' ? 'rgb(42, 42, 42)' : 'antiquewhite'}
      onSubmit={handleSubmit}
    >
      <label>
        Title
        <input
          onChange={(event) => setTitle(event.target.value)}
          value={title}
        />
      </label>
      <label>
        Content
        <textarea
          rows={4}
          onChange={(event) => setContent(event.target.value)}
          value={content}
        />
      </label>
      <PrimaryButton type='submit'>Publish</PrimaryButton>
    </Form>
  );
}

const Form = styled.form<MyComponentProps>`
  display: flex;
  flex-direction: column;
  width: 350px;

  label {
    width: 100%;
    display: flex;
    flex-direction: column;
    border-bottom: 1px solid ${(props) => props.ThemeColor};
    margin-bottom: 20px;
  }

  input {
    width: 250px;
    background-color: inherit;
    outline: none;
    border: none;
    padding: 5px;
  }

  textarea {
    width: 250px;
    background-color: inherit;
    outline: none;
    border: none;
    padding: 5px;
  }
`;

export default BlogForm;
