import React from 'react';
import { useEffect, useState } from 'react';
import moment, { Moment } from 'moment';
import { useContext } from 'react';
import { SomeContext } from './SomeContext';
import styled from 'styled-components';
import { AnimatePresence, motion, easeInOut } from 'framer-motion';
import PrimaryButton from './PrimaryButton';

interface Post {
  title: string;
  date: Moment;
  content: string;
}

interface MyComponentProps {
  ThemeColor: string;
}

const thisDate = moment().format('YYYY-MM-DD');

function BlogView() {
  const { loggedIn } = useContext(SomeContext) ?? {};
  const blog = useContext(SomeContext)?.blog;
  const { theme } = useContext(SomeContext) ?? {};
  const [posts, setPosts] = useState<Post[]>([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [isNotLoggedIn, setIsNotLoggedIn] = useState(true);
  const [showForm, setShowForm] = useState(false);

  const storedPosts = JSON.parse(
    localStorage.getItem('posts') || '[]',
  ) as Post[];

  useEffect(() => {
    if (storedPosts) {
      const storedList = storedPosts.map((post) => ({
        title: post.title,
        date: moment(post.date),
        content: post.content,
      }));
      setPosts(storedList);
    }
    if (blog) {
      const blogList = blog.map((post) => ({
        title: post.title,
        date: moment(post.date),
        content: post.content,
      }));
      localStorage.setItem('posts', JSON.stringify(blogList));
      setPosts(blogList);
    }
  }, []);

  function handleSubmit(event: React.FormEvent<HTMLFormElement>): void {
    event.preventDefault();
    if (loggedIn) {
      const newPost = {
        title: title,
        date: moment(thisDate),
        content: content,
      };
      setPosts([newPost, ...posts]);
      localStorage.setItem('posts', JSON.stringify([newPost, ...posts]));
      setTitle('');
      setContent('');
    }
  }

  useEffect(() => {
    if (loggedIn) {
      setIsNotLoggedIn(false);
    }
  }, [loggedIn]);

  function handleClick() {
    setShowForm(true);
  }

  useEffect(() => {
    console.log(posts);
  }, [posts]);

  return (
    <AnimatePresence>
      <motion.div
        initial={{ x: 200, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        exit={{ x: -300, opacity: 0 }}
        transition={{ delay: 0.3, ease: easeInOut, duration: 1.3 }}
      >
        <main>
          <HeaderDiv>
            <h1>Blog</h1>
            <div>
              <hr />
            </div>
          </HeaderDiv>
          {posts.map((post) => (
            <BlogDiv key={post.title}>
              <h2>{post.title}</h2>
              <p>{moment(post.date).format('YYYY-MM-DD')}</p>
              <p>{post.content}</p>
              <hr />
            </BlogDiv>
          ))}
          <FormDiv>
            {showForm ? (
              <Form
                ThemeColor={
                  theme === 'light' ? 'rgb(42, 42, 42)' : 'antiquewhite'
                }
                onSubmit={handleSubmit}
              >
                <label>
                  Title
                  <input
                    onChange={(event) => setTitle(event.target.value)}
                    value={title}
                    required
                  />
                </label>
                <label>
                  Content
                  <textarea
                    rows={4}
                    onChange={(event) => setContent(event.target.value)}
                    value={content}
                    required
                  />
                </label>
                <PrimaryButton type='submit'>Publish</PrimaryButton>
              </Form>
            ) : (
              <AddButton
                ThemeColor={
                  theme === 'light' ? 'rgb(42, 42, 42)' : 'antiquewhite'
                }
                onClick={handleClick}
                disabled={isNotLoggedIn ? true : false}
              >
                Add blogpost
              </AddButton>
            )}
          </FormDiv>
        </main>
      </motion.div>
    </AnimatePresence>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;

  div {
    width: 70%;
    padding-bottom: 1.2%;
  }

  @media (max-width: 500px) {
    div {
      width: 60%;
      padding-bottom: 0;
    }
  }
`;

const BlogDiv = styled.div`
  margin-top: 60px;

  h2 {
    font-size: calc(50% + 4vw);
  }

  p {
    width: 80%;
  }

  hr {
    margin: 40px 0 40px 0;
  }
`;

const FormDiv = styled.div`
  margin-top: 60px;
  display: flex;
  justify-content: center;
`;

const Form = styled.form<MyComponentProps>`
  display: flex;
  flex-direction: column;
  width: 70%;
  min-width: 300px;
  max-width: 450px;

  label {
    width: 100%;
    max-width: 300px;
    display: flex;
    flex-direction: column;
    border-bottom: 1px solid ${(props) => props.ThemeColor};
    margin-bottom: 20px;
  }

  label:focus-within {
    border-bottom: 1px solid rgb(254, 105, 5);
    color: rgb(254, 105, 5);
  }

  input {
    width: 250px;
    background-color: inherit;
    outline: none;
    border: none;
    padding: 5px;
  }

  textarea {
    width: 350px;
    background-color: inherit;
    outline: none;
    border: none;
    padding: 5px;
  }
`;

const AddButton = styled.button<MyComponentProps>`
  height: 40px;
  width: 180px;
  background-color: inherit;
  border: 1.5px solid ${(props) => props.ThemeColor};
  border-radius: 30px;
  font-size: 0.9rem;
  transition: 0.2s ease-in;

  &:hover {
    background-color: rgb(254, 105, 5);
  }
`;

export default BlogView;
