import React from "react";
import { useEffect, useState } from "react";
// import * as Icon from "react-bootstrap-icons";
import moment from "moment";
import { useContext } from "react";
import { SomeContext } from "./SomeContext";
import type { Details } from "./SomeContext";
import styled from "styled-components";

function AboutView() {
  const details = useContext(SomeContext)?.details;
  const misc = useContext(SomeContext)?.misc;
  const [years, setYears] = useState("");

  // useEffect(() => {
  //   fetch("/klara.json")
  //     .then((response) => response.json())
  //     .then((result) => {
  //       setMisc(result.about.misc);
  //       setDetails(result.about.details);
  //       console.log(result.about);
  //     });
  // }, []);

  useEffect(() => {
    if (details) {
      const diffYears = moment().diff(details.age, "years") * -1;
      setYears(diffYears.toString());
    }
  }, [details]);

  return (
    <main>
      <HeaderDiv>
        <h1>About</h1>
        <div>
          <hr />
        </div>
      </HeaderDiv>
      <AboutDiv>
        {misc && misc !== null && (
          <div>
            <p>{misc.introdescription}</p>
            {misc.description.map((paragraph, index) => (
              <p key={index}>{paragraph}</p>
            ))}
          </div>
        )}
        {details && details !== null && (
          <div>
            <img alt="Klara" src={details.image[0]}></img>
            <h2>{details.name}</h2>
            {years !== "" && <p>{years} years</p>}
            <ul>
              <li>{details.email}</li>
            </ul>
          </div>
        )}
      </AboutDiv>
    </main>
  );
}

const HeaderDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 70%;
  }

  hr {
    margin-bottom: 25px;
  }
`;

const AboutDiv = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 60px;

  div {
    width: 50%;
  }
`;

export default AboutView;
