import React from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import styled from "styled-components";
import { Project } from "./WorkView";

interface WorkListProps {
  projects: Project[];
}

interface CardProps {
  image: string;
}

function WorkList(props: WorkListProps) {
  console.log(props.projects);
  return (
    <Cards className="WorkList">
      {props.projects.map((project) => (
        <Link className="WorkList-card" to={project.id} key={project.id}>
          <Card image={project.image}>
            <h3>{project.name}</h3>
            <div className="WorkList-container">
              <div></div>
              {/* <div>
                {project.languages.map((language) => (
                  <span key={language}> {language} / </span>
                ))}
              </div> */}
            </div>
          </Card>
        </Link>
      ))}
    </Cards>
  );
}

WorkList.propTypes = {
  projects: PropTypes.array,
};

const Cards = styled.div`
  display: flex;
`;

const Card = styled.div<CardProps>`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: rgba(211, 206, 194, 0.7);
  width: 300px;
  height: 300px;
  margin-right: 20px;
  padding: 10px;

  .WorkList-container {
    visibility: hidden;
  }

  h3,
  p,
  span {
    visibility: visible;
  }

  &:hover {
    .WorkList-container {
      visibility: visible;
      width: 100%;
      height: 100%;
      margin: auto;
      background-image: url(${(props) => props.image});
      background-repeat: no-repeat;
      background-size: cover;

      animation: backGround 0.5s linear;
    }

    h3,
    p,
    span {
      visibility: hidden;
    }
  }

  @keyframes backGround {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }

  div {
    text-align: center;
  }
`;

export default WorkList;
